class CreateSuratPerintahJalanRequest extends FormRequest
{

    /**
     * Determine if the user is authorized to make this request.
     *
     * @return bool
     */
    public function authorize()
    {
        return true;
    }

    /**
     * Get the validation rules that apply to the request.
     *
     * @return array
     */
    public function rules()
    {
        return [
            'nama_pengemudi' => "required|max:20",
            'tujuan' => "required|max:255",
            'total_biaya' => "required|max:255",
            'lama_perjalanan' => "required|max:255",
            'tanggal_berangkat' => "required|max:255",
            'tanggal_kembali' => "required|max:255",
            'jam_berangkat' => "required|max:255",
            'jam_kembali' => "required|max:255",
            'pengisian_bbm' => "required|max:10",
            'penanggung_jawab' => "required|max:255",
            'penanggung_jawab_pool' => "required|max:255"
        ];
    }
}
